const prompt = require('prompt-sync')();
function Mhs (nama, energi){
    let mhs = {};
    mhs.nama = nama;
    mhs.energi = energi;

    mhs.makan = function (porsi){
        this.energi += porsi;
        console.log('Halo ${this.nama}');
    }
    mhs.main = function(jam){
        this.energi -= jam;
        console.log('Halo $ {this.nama}')
    }
return mhs;
}
let aku = mhs ('Aku', 10);
let kamu = mhs ('Kamu');